<script>
	import {createEventDispatcher} from 'svelte';
	import {makeStyleVars} from '@svizzle/dom';

	const dispatch = createEventDispatcher();
	const defaultTheme = {
		width: '50px',
		height: '24px',
		color: 'grey',
		backgroundColor: 'white',
		knobColor: 'lightgrey'
	}

	export let current;
	export let values;
	export let theme;
	export let showLabels = true;

	$: value = current;
	$: theme = {...defaultTheme, theme};
	$: style = makeStyleVars(theme);

	function toggle() {
		value = value === values[0] ? values[1] : values[0];
		dispatch('toggled', value);
	}
</script>

<div class="switch" {style}>
	<fieldset role="radiogroup">
		{#if showLabels}
			<label for="one" class:greyed={value !== values[0]}>{values[0]}</label>
		{/if}
		<span class="wrapper" on:click={toggle}>
			<input type="radio" id="one" bind:group={value} value="{values[0]}">
			<input type="radio" id="two" bind:group={value} value="{values[1]}">
			<span aria-hidden="true" class="bkg"></span>
			<span aria-hidden="true" class="knob"></span>
		</span>
		{#if showLabels}
			<label for="two" class:greyed={value !== values[1]}>{values[1]}</label>
		{/if}
	</fieldset>
</div>

<style>
	fieldset {
		border: none;
		user-select: none;
		padding: 0;
	}

	.switch {
		cursor: pointer;
		display: flex;
		align-items: center;
	}
	.switch label {
		font-size: 16px;
		margin: 0 .5em 0;
		cursor: pointer;
	}
	.switch label.greyed {
		opacity: 0.5;
	}
	.switch input[type="radio"] {
		display: inline-block;
		margin-right: -2px;
		width: 50%;
		height: 100%;
		opacity: 0;
		position: relative;
		z-index: 1;
		cursor: pointer;
	}

	.wrapper {
		font-size: var(--baseSize); /* FIXME this var doesn't exist */
		display: inline-block;
		vertical-align: middle;
		width: var(--width);
		height: var(--height);
		border-radius: calc(2 * var(--height));
		border: 1px solid var(--color);
		position: relative;
	}
	.knob {
		display: block;
		position: absolute;
		top: 0;
		left: 0;
		right: 100%;
		width: 50%;
		height: 100%;
		border-radius: 50%;
		border: 1px solid var(--color);
		transition: all 0.1s ease-out;
		z-index: 2;
		background: var(--knobColor);
	}

	.bkg {
		display: block;
		position: absolute;
		top: 0;
		left: 0;
		width: 100%;
		height: 100%;
		z-index: 0;
		border-radius: 2em;
		background-color: var(--backgroundColor);
		transition: all 0.2s ease-out;
		cursor: pointer;
	}

	#two:checked ~ .knob {
		right: 0;
		left: 50%;
	}

	#two:checked ~ .bkg {
		background-color: #fff;
	}
</style>
